def combination(take_poke):
    if len(take_poke) == 1:
        return take_poke
    else:
        result = []
        for i in range(len(take_poke)):
            rest_poke = take_poke[:i]+take_poke[i+1:]
            a = combination(rest_poke)
            if len(a) == 1:
                a.insert(0, take_poke[i])
                result.append(a)
            else:
                for j in range(len(a)):
                    a[j].insert(0, take_poke[i])
                    result.append(a[j])
        return result
